import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { addCart, addOrder, addPayment, getAllDiscounts } from '../services/api';

const STEPS = ['Cart Review', 'Checkout', 'Payment', 'Confirmation'];

export default function CartPage() {
  const { cartItems, removeFromCart, updateQty, clearCart, cartTotal } = useCart();
  const { user } = useAuth();
  const navigate = useNavigate();

  const [step, setStep] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [orderId, setOrderId] = useState(null);
  const [paymentMethod, setPaymentMethod] = useState('CARD');
  const [deliveryAddress, setDeliveryAddress] = useState(user?.address || '');

  // Discount state
  const [discounts, setDiscounts] = useState([]);       // all from backend
  const [showOffers, setShowOffers] = useState(false);  // toggle list
  const [discountCode, setDiscountCode] = useState('');
  const [appliedDiscount, setAppliedDiscount] = useState(null);
  const [discountMsg, setDiscountMsg] = useState('');

  // Load available discounts from backend
  useEffect(() => {
    getAllDiscounts()
      .then(r => setDiscounts(r.data))
      .catch(() => {});
  }, []);

  const discountAmt = appliedDiscount ? (cartTotal * appliedDiscount.discountPercentage) / 100 : 0;
  const finalTotal = Math.max(0, cartTotal - discountAmt);

  const applyCode = (code) => {
    const c = (code || discountCode).trim().toUpperCase();
    const found = discounts.find(d => d.discountOfferType?.toUpperCase() === c);
    if (found) {
      setAppliedDiscount(found);
      setDiscountCode(found.discountOfferType);
      setDiscountMsg(`‚úÖ "${found.discountOfferType}" applied ‚Äî ${found.discountPercentage}% off!`);
      setShowOffers(false);
    } else {
      setAppliedDiscount(null);
      setDiscountMsg('‚ùå Invalid discount code.');
    }
  };

  const handleOfferClick = (offer) => {
    setDiscountCode(offer.discountOfferType);
    applyCode(offer.discountOfferType);
  };

  const removeDiscount = () => {
    setAppliedDiscount(null);
    setDiscountCode('');
    setDiscountMsg('');
  };

  const handlePlaceOrder = async () => {
    if (!deliveryAddress.trim()) { setError('Please enter a delivery address.'); return; }
    setLoading(true); setError('');
    try {
      const cartRes = await addCart({
        cartTotal: finalTotal,
        user: { userId: user.userId },
        ...(appliedDiscount ? { discount: { discountId: appliedDiscount.discountId } } : {}),
      });
      const orderRes = await addOrder({
        orderTotal: finalTotal,
        orderStatus: 'CONFIRMED',
        user: { userId: user.userId },
        cart: { cartId: cartRes.data.cartId },
      });
      setOrderId(orderRes.data.orderId);
      setStep(2);
    } catch { setError('Failed to place order. Please ensure the backend is running.'); }
    finally { setLoading(false); }
  };

  const handlePayment = async () => {
    setLoading(true); setError('');
    try {
      await addPayment({
        paymentMethod,
        paymentStatus: 'SUCCESS',
        paymentAmount: finalTotal,
        order: { orderId },
      });
      clearCart();
      setStep(3);
    } catch { setError('Payment failed. Please try again.'); }
    finally { setLoading(false); }
  };

  if (cartItems.length === 0 && step !== 3) return (
    <div className="page">
      <div className="empty-state">
        <div className="emo">üõí</div>
        <h3>Your cart is empty</h3>
        <p>Add some fresh groceries to get started!</p>
        <button className="btn btn-primary" onClick={() => navigate('/products')}>Browse Products</button>
      </div>
    </div>
  );

  const getEmoji = (cat) => {
    if (!cat) return 'üõí';
    const k = cat.toLowerCase();
    if (k.includes('fruit')) return 'üçé';
    if (k.includes('veg')) return 'ü•¶';
    if (k.includes('dairy')) return 'ü•õ';
    if (k.includes('bakery')) return 'üçû';
    return 'üõí';
  };

  return (
    <div className="page">
      <div className="page-header">
        <h1 className="page-title">Checkout</h1>
        <p className="page-subtitle">Complete your order in a few easy steps</p>
      </div>

      {/* Steps */}
      <div className="checkout-steps">
        {STEPS.map((s, i) => (
          <React.Fragment key={s}>
            <div className={`step ${i === step ? 'active' : i < step ? 'done' : ''}`}>
              <div className="step-circle">{i < step ? '‚úì' : i + 1}</div>
              <div className="step-label">{s}</div>
            </div>
            {i < STEPS.length - 1 && <div className={`step-line ${i < step ? 'done' : ''}`} />}
          </React.Fragment>
        ))}
      </div>

      {error && <div className="alert alert-error">‚ö†Ô∏è {error}</div>}

      {/* ‚îÄ‚îÄ STEP 0: Cart Review ‚îÄ‚îÄ */}
      {step === 0 && (
        <div className="cart-layout">
          {/* Items */}
          <div className="cart-card">
            <div className="cart-card-header">
              <span className="cart-card-title">Your Items ({cartItems.length})</span>
              <button className="btn btn-ghost btn-sm" onClick={() => { if (window.confirm('Clear cart?')) clearCart(); }}>
                üóë Clear
              </button>
            </div>
            {cartItems.map(item => (
              <div key={item.productId} className="cart-item">
                <div className="cart-item-icon">{getEmoji(item.productCategory)}</div>
                <div className="cart-item-info">
                  <div className="cart-item-name">{item.productName}</div>
                  <div className="cart-item-unit">‚Çπ{item.productPrice?.toFixed(2)} each</div>
                </div>
                <div className="qty-ctrl">
                  <button className="qty-btn" onClick={() => updateQty(item.productId, item.qty - 1)}>‚àí</button>
                  <span className="qty-num">{item.qty}</span>
                  <button className="qty-btn" onClick={() => updateQty(item.productId, item.qty + 1)}>+</button>
                </div>
                <div className="cart-item-total">‚Çπ{(item.productPrice * item.qty).toFixed(2)}</div>
                <button className="del-btn" onClick={() => removeFromCart(item.productId)}>üóë</button>
              </div>
            ))}
          </div>

          {/* Summary */}
          <div className="summary-panel">
            <div className="summary-header">
              <div className="summary-title">Order Summary</div>
            </div>
            <div className="summary-body">

              {/* Discount Section */}
              <div className="discount-section">
                <div className="discount-label">üéÅ Discount Code</div>

                <div className="discount-input-row">
                  <input
                    placeholder="Enter code‚Ä¶"
                    value={discountCode}
                    onChange={e => setDiscountCode(e.target.value)}
                    onKeyDown={e => e.key === 'Enter' && applyCode()}
                  />
                  <button className="btn btn-outline btn-sm" onClick={() => applyCode()}>Apply</button>
                </div>

                {discountMsg && (
                  <div style={{ fontSize: '0.8rem', marginBottom: '0.5rem', color: appliedDiscount ? 'var(--jade)' : 'var(--red)' }}>
                    {discountMsg}
                    {appliedDiscount && (
                      <button onClick={removeDiscount} style={{ marginLeft: '0.5rem', background: 'none', border: 'none', color: 'var(--red)', cursor: 'pointer', fontSize: '0.78rem', fontWeight: 600 }}>
                        Remove
                      </button>
                    )}
                  </div>
                )}

                {/* Show available offers from backend */}
                {discounts.length > 0 && (
                  <>
                    <button className="discount-available-toggle" onClick={() => setShowOffers(!showOffers)}>
                      üè∑ {showOffers ? 'Hide' : 'View'} available offers ({discounts.length})
                      <span style={{ fontSize: '0.7rem' }}>{showOffers ? '‚ñ≤' : '‚ñº'}</span>
                    </button>
                    {showOffers && (
                      <div className="discount-offers-list">
                        {discounts.map(d => (
                          <div key={d.discountId} className="discount-offer-item" onClick={() => handleOfferClick(d)}>
                            <div>
                              <div className="offer-code">{d.discountOfferType}</div>
                              <div className="offer-apply">Click to apply</div>
                            </div>
                            <div className="offer-pct">{d.discountPercentage}% OFF</div>
                          </div>
                        ))}
                      </div>
                    )}
                  </>
                )}
              </div>

              <div className="divider" />
              <div className="summary-row"><span>Subtotal</span><span>‚Çπ{cartTotal.toFixed(2)}</span></div>
              {appliedDiscount && (
                <div className="summary-row discount">
                  <span>Discount ({appliedDiscount.discountPercentage}%)</span>
                  <span>‚àí‚Çπ{discountAmt.toFixed(2)}</span>
                </div>
              )}
              <div className="divider" />
              <div className="summary-row total"><span>Total</span><span>‚Çπ{finalTotal.toFixed(2)}</span></div>
              <br />
              <button className="btn btn-primary btn-full" onClick={() => setStep(1)}>
                Proceed to Checkout ‚Üí
              </button>
            </div>
          </div>
        </div>
      )}

      {/* ‚îÄ‚îÄ STEP 1: Delivery ‚îÄ‚îÄ */}
      {step === 1 && (
        <div className="section-card" style={{ maxWidth: 560 }}>
          <h3 style={{ fontFamily: 'Cormorant Garamond', fontSize: '1.4rem', color: 'var(--forest)', marginBottom: '1.5rem' }}>
            üè† Delivery Details
          </h3>
          <div className="form-group">
            <label className="form-label">Delivery Address</label>
            <textarea className="form-control" rows={3}
              placeholder="Enter your full delivery address"
              value={deliveryAddress} onChange={e => setDeliveryAddress(e.target.value)} />
          </div>
          <div className="summary-row total" style={{ marginBottom: '1.5rem' }}>
            <span>Order Total</span><span>‚Çπ{finalTotal.toFixed(2)}</span>
          </div>
          <div style={{ display: 'flex', gap: '0.75rem' }}>
            <button className="btn btn-ghost" onClick={() => setStep(0)}>‚Üê Back</button>
            <button className="btn btn-primary" disabled={loading} onClick={handlePlaceOrder}>
              {loading ? '‚è≥ Placing‚Ä¶' : '‚úÖ Place Order'}
            </button>
          </div>
        </div>
      )}

      {/* ‚îÄ‚îÄ STEP 2: Payment ‚îÄ‚îÄ */}
      {step === 2 && (
        <div className="section-card" style={{ maxWidth: 560 }}>
          <h3 style={{ fontFamily: 'Cormorant Garamond', fontSize: '1.4rem', color: 'var(--forest)', marginBottom: '1.5rem' }}>
            üí≥ Complete Payment
          </h3>
          <div className="alert alert-success">Order #{orderId} placed! Choose payment to confirm.</div>
          <div className="form-group">
            <label className="form-label">Payment Method</label>
            <select className="form-control" value={paymentMethod} onChange={e => setPaymentMethod(e.target.value)}>
              <option value="CARD">üí≥ Credit / Debit Card</option>
              <option value="UPI">üì± UPI</option>
              <option value="WALLET">üëõ Wallet</option>
              <option value="COD">üö™ Cash on Delivery</option>
            </select>
          </div>
          <div className="summary-row total" style={{ marginBottom: '1.5rem' }}>
            <span>Amount to Pay</span><span>‚Çπ{finalTotal.toFixed(2)}</span>
          </div>
          <button className="btn btn-gold btn-full" disabled={loading} onClick={handlePayment}>
            {loading ? '‚è≥ Processing‚Ä¶' : `Pay ‚Çπ${finalTotal.toFixed(2)} via ${paymentMethod}`}
          </button>
        </div>
      )}

      {/* ‚îÄ‚îÄ STEP 3: Confirmation ‚îÄ‚îÄ */}
      {step === 3 && (
        <div className="section-card" style={{ textAlign: 'center', padding: '4rem 2rem' }}>
          <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üéâ</div>
          <h2 style={{ fontFamily: 'Cormorant Garamond', fontSize: '2rem', color: 'var(--forest)', marginBottom: '0.5rem' }}>
            Order Confirmed!
          </h2>
          <p style={{ color: 'var(--text-soft)', marginBottom: '0.4rem' }}>
            Your order #{orderId} has been placed and payment received.
          </p>
          <p style={{ color: 'var(--text-pale)', fontSize: '0.9rem', marginBottom: '2rem' }}>
            You'll receive a notification once it's out for delivery üöö
          </p>
          <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center' }}>
            <button className="btn btn-ghost" onClick={() => navigate('/orders')}>View Orders</button>
            <button className="btn btn-primary" onClick={() => navigate('/products')}>Continue Shopping</button>
          </div>
        </div>
      )}
    </div>
  );
}
